  constructor() {
    this.searchReset = true;
    this.compose = false;
    this.isVisible = false;
    this.searchColor = "";
  }

  ngOnInit(): void {
  }

  async showFilter(){
    if (this.isVisible)
      return;
    setTimeout(() => {
      this.isVisible = true;
    });
  }

  async hideFilter(){
    if (!this.isVisible)
      return;
    setTimeout(() => {
      this.isVisible = false;
      this.searchColor = "";
    });
  }

  async whitenBar(){
    setTimeout(() => {
      this.searchColor = "white";
      if (this.searchReset){
        console.log("sdfgdfg");
        this.searchBar.nativeElement.value = "";
      }
    }, 250);
  }

  async darkenBar(){
    setTimeout(() => {
      if (!this.isVisible)
        this.searchColor = "";
      
      if (this.searchBar.nativeElement.value === ""){
        this.searchReset = true;
        this.searchBar.nativeElement.value = "Search in mail";
      } else {
        if (this.searchReset && this.searchBar.nativeElement.value !== "Search in mail")
          this.searchReset = false;
      }
    });
  }

  async composeIt(){
    if (this.compose)
      return;
    setTimeout(() => {
      this.compose = true;
    });
  }
}
